/*Data for the table `article` */

insert  into `article`(`ar_id`,`ar_title`,`contentHtml`,`contentTxt`) values (1,'关于SAPA','<h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于SAPA</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\">说起&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，很多地方翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，容易和东马（&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;）的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;边陲&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/191945.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">老街省</a>&nbsp;的一个山中小镇。两者的英文缩写也完全不同，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;是SAPA，东马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，这样和马蜂窝本身对该地的译名保持统一。<br/><br/>在网络上很容易搜到有&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/60211.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">关东</a>&nbsp;马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州的旅游信息攻略，而关于&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;还算是如今&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/52314.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">亚洲</a>&nbsp;面孔多是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12684.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">台湾</a>&nbsp;、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，在&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/21536.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">中国</a>&nbsp;旅游圈名气不响，但是在国际上，特别是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/14383.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">欧洲</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;本土，绝对是响当当的旅游朝圣地。如果你问&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;人，自己的国家哪里最美，相信一半的人会说&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16315.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">岘港</a>&nbsp;，而我们国人耳熟能详的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16102.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">芽庄</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16105.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">美奈</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16379.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">富国岛</a>&nbsp;，根本不会排在其中。同样在欧美旅游圈，来到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的旅游基础建设以&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。<br/><br/>言归正传，我们先来认识一下游记的主角。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，也叫沙坝、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/65633.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南老街</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;县的县城所在地，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;距离老街市约38公里，老街市与我国&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">云南</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/11871.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">红河</a>&nbsp;哈尼族彝族自治州&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/141503.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">河口瑶族自治县</a>&nbsp;交界，我国游客可通过河口---老街口岸进入&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;，然后辗转&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;与&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;民主&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/63525.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">共和</a>&nbsp;国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;作为旅游区重新向世界游客开放是在1993年以后，随着&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;政局的稳定，经济的发展，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;争做国际化旅游区的步伐如今已经大大加快。<br/><br/></p><h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于写这篇游记的原因</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\"><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10466.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">长沙</a>&nbsp;到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，多数人都是一脸懵逼。“&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;？”为此楼主又要不厌其烦的介绍此&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;非彼&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。<br/><img src=\"/tourOne/image/20190829/1567055967177032859.jpg\" title=\"1567055967177032859.jpg\" alt=\"wKgE2l1GNtKAQHOqAA5qv1MAY-M544.jpg\"/>胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。<br/><img src=\"/tourOne/image/20190829/1567055972728013349.jpg\" title=\"1567055972728013349.jpg\" alt=\"wKgE2l1HheGAaE6OAAXfqZoi9kY776.jpg\"/>首先：我们来领略一路上的神奇风光。</p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117848.html\" style=\"color: rgb(255, 157, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055955749098933.jpg\" alt=\"\"/></a></p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117849.html\" style=\"color: rgb(255, 138, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative; zoom: 1;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055958872025478.jpg\" alt=\"\"/></a></p><p><br/></p>','关于SAPA说起 萨帕 ，很多地方翻译成 沙巴 ，容易和东马（ 马来西亚 ）的 沙巴 州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是 越南 边陲 老街省 的一个山中小镇。两者的英文缩写也完全不同， 越南 沙巴 是SAPA，东马 沙巴 州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将 越南 的 沙巴 翻译成 萨帕 ，这样和马蜂窝本身对该地的译名保持统一。在网络上很容易搜到有 关东 马 沙巴 州的旅游信息攻略，而关于 越南 萨帕 的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因， 萨帕 还算是如今 越南 旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少， 亚洲 面孔多是 台湾 、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。越南 的 萨帕 ，在 中国 旅游圈名气不响，但是在国际上，特别是 欧洲 和 越南 本土，绝对是响当当的旅游朝圣地。如果你问 越南 人，自己的国家哪里最美，相信一半的人会说 萨帕 和 岘港 ，而我们国人耳熟能详的 芽庄 、 美奈 、 富国岛 ，根本不会排在其中。同样在欧美旅游圈，来到 越南 旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性， 萨帕 的旅游基础建设以 越南 的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。言归正传，我们先来认识一下游记的主角。 萨帕 ，也叫沙坝、 沙巴 ，是 越南老街 省 萨帕 县的县城所在地， 萨帕 距离老街市约38公里，老街市与我国 云南 省 红河 哈尼族彝族自治州 河口瑶族自治县 交界，我国游客可通过河口---老街口岸进入 越南 ，然后辗转 萨帕 。萨帕 与 越南 第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期， 法国 殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后 法国 殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代 越南 民主 共和 国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。 萨帕 作为旅游区重新向世界游客开放是在1993年以后，随着 越南 政局的稳定，经济的发展， 萨帕 争做国际化旅游区的步伐如今已经大大加快。关于写这篇游记的原因萨帕 一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从 长沙 到 萨帕 理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他 萨帕 ，多数人都是一脸懵逼。“ 马来西亚 ？”为此楼主又要不厌其烦的介绍此 萨帕 非彼 沙巴 ，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解 萨帕 的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。首先：我们来领略一路上的神奇风光。'),(2,'关于SAPA','<h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于SAPA</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\">说起&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，很多地方翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，容易和东马（&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;）的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;边陲&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/191945.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">老街省</a>&nbsp;的一个山中小镇。两者的英文缩写也完全不同，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;是SAPA，东马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，这样和马蜂窝本身对该地的译名保持统一。<br/><br/>在网络上很容易搜到有&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/60211.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">关东</a>&nbsp;马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州的旅游信息攻略，而关于&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;还算是如今&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/52314.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">亚洲</a>&nbsp;面孔多是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12684.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">台湾</a>&nbsp;、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，在&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/21536.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">中国</a>&nbsp;旅游圈名气不响，但是在国际上，特别是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/14383.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">欧洲</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;本土，绝对是响当当的旅游朝圣地。如果你问&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;人，自己的国家哪里最美，相信一半的人会说&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16315.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">岘港</a>&nbsp;，而我们国人耳熟能详的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16102.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">芽庄</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16105.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">美奈</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16379.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">富国岛</a>&nbsp;，根本不会排在其中。同样在欧美旅游圈，来到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的旅游基础建设以&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。<br/><br/>言归正传，我们先来认识一下游记的主角。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，也叫沙坝、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/65633.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南老街</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;县的县城所在地，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;距离老街市约38公里，老街市与我国&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">云南</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/11871.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">红河</a>&nbsp;哈尼族彝族自治州&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/141503.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">河口瑶族自治县</a>&nbsp;交界，我国游客可通过河口---老街口岸进入&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;，然后辗转&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;与&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;民主&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/63525.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">共和</a>&nbsp;国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;作为旅游区重新向世界游客开放是在1993年以后，随着&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;政局的稳定，经济的发展，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;争做国际化旅游区的步伐如今已经大大加快。<br/><br/></p><h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于写这篇游记的原因</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\"><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10466.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">长沙</a>&nbsp;到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，多数人都是一脸懵逼。“&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;？”为此楼主又要不厌其烦的介绍此&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;非彼&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。<br/><img src=\"/tourOne/image/20190829/1567055967177032859.jpg\" title=\"1567055967177032859.jpg\" alt=\"wKgE2l1GNtKAQHOqAA5qv1MAY-M544.jpg\"/>胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。<br/><img src=\"/tourOne/image/20190829/1567055972728013349.jpg\" title=\"1567055972728013349.jpg\" alt=\"wKgE2l1HheGAaE6OAAXfqZoi9kY776.jpg\"/>首先：我们来领略一路上的神奇风光。</p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117848.html\" style=\"color: rgb(255, 157, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055955749098933.jpg\" alt=\"\"/></a></p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117849.html\" style=\"color: rgb(255, 138, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative; zoom: 1;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055958872025478.jpg\" alt=\"\"/></a></p><p><br/></p>','关于SAPA说起 萨帕 ，很多地方翻译成 沙巴 ，容易和东马（ 马来西亚 ）的 沙巴 州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是 越南 边陲 老街省 的一个山中小镇。两者的英文缩写也完全不同， 越南 沙巴 是SAPA，东马 沙巴 州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将 越南 的 沙巴 翻译成 萨帕 ，这样和马蜂窝本身对该地的译名保持统一。在网络上很容易搜到有 关东 马 沙巴 州的旅游信息攻略，而关于 越南 萨帕 的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因， 萨帕 还算是如今 越南 旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少， 亚洲 面孔多是 台湾 、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。越南 的 萨帕 ，在 中国 旅游圈名气不响，但是在国际上，特别是 欧洲 和 越南 本土，绝对是响当当的旅游朝圣地。如果你问 越南 人，自己的国家哪里最美，相信一半的人会说 萨帕 和 岘港 ，而我们国人耳熟能详的 芽庄 、 美奈 、 富国岛 ，根本不会排在其中。同样在欧美旅游圈，来到 越南 旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性， 萨帕 的旅游基础建设以 越南 的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。言归正传，我们先来认识一下游记的主角。 萨帕 ，也叫沙坝、 沙巴 ，是 越南老街 省 萨帕 县的县城所在地， 萨帕 距离老街市约38公里，老街市与我国 云南 省 红河 哈尼族彝族自治州 河口瑶族自治县 交界，我国游客可通过河口---老街口岸进入 越南 ，然后辗转 萨帕 。萨帕 与 越南 第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期， 法国 殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后 法国 殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代 越南 民主 共和 国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。 萨帕 作为旅游区重新向世界游客开放是在1993年以后，随着 越南 政局的稳定，经济的发展， 萨帕 争做国际化旅游区的步伐如今已经大大加快。关于写这篇游记的原因萨帕 一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从 长沙 到 萨帕 理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他 萨帕 ，多数人都是一脸懵逼。“ 马来西亚 ？”为此楼主又要不厌其烦的介绍此 萨帕 非彼 沙巴 ，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解 萨帕 的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。首先：我们来领略一路上的神奇风光。'),(3,'关于SAPA','<h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于SAPA</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\">说起&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，很多地方翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，容易和东马（&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;）的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;边陲&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/191945.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">老街省</a>&nbsp;的一个山中小镇。两者的英文缩写也完全不同，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;是SAPA，东马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，这样和马蜂窝本身对该地的译名保持统一。<br/><br/>在网络上很容易搜到有&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/60211.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">关东</a>&nbsp;马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州的旅游信息攻略，而关于&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;还算是如今&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/52314.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">亚洲</a>&nbsp;面孔多是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12684.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">台湾</a>&nbsp;、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，在&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/21536.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">中国</a>&nbsp;旅游圈名气不响，但是在国际上，特别是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/14383.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">欧洲</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;本土，绝对是响当当的旅游朝圣地。如果你问&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;人，自己的国家哪里最美，相信一半的人会说&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16315.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">岘港</a>&nbsp;，而我们国人耳熟能详的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16102.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">芽庄</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16105.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">美奈</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16379.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">富国岛</a>&nbsp;，根本不会排在其中。同样在欧美旅游圈，来到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的旅游基础建设以&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。<br/><br/>言归正传，我们先来认识一下游记的主角。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，也叫沙坝、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/65633.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南老街</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;县的县城所在地，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;距离老街市约38公里，老街市与我国&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">云南</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/11871.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">红河</a>&nbsp;哈尼族彝族自治州&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/141503.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">河口瑶族自治县</a>&nbsp;交界，我国游客可通过河口---老街口岸进入&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;，然后辗转&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;与&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;民主&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/63525.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">共和</a>&nbsp;国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;作为旅游区重新向世界游客开放是在1993年以后，随着&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;政局的稳定，经济的发展，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;争做国际化旅游区的步伐如今已经大大加快。<br/><br/></p><h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于写这篇游记的原因</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\"><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10466.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">长沙</a>&nbsp;到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，多数人都是一脸懵逼。“&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;？”为此楼主又要不厌其烦的介绍此&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;非彼&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。<br/><img src=\"/tourOne/image/20190829/1567055967177032859.jpg\" title=\"1567055967177032859.jpg\" alt=\"wKgE2l1GNtKAQHOqAA5qv1MAY-M544.jpg\"/>胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。<br/><img src=\"/tourOne/image/20190829/1567055972728013349.jpg\" title=\"1567055972728013349.jpg\" alt=\"wKgE2l1HheGAaE6OAAXfqZoi9kY776.jpg\"/>首先：我们来领略一路上的神奇风光。</p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117848.html\" style=\"color: rgb(255, 157, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055955749098933.jpg\" alt=\"\"/></a></p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117849.html\" style=\"color: rgb(255, 138, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative; zoom: 1;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055958872025478.jpg\" alt=\"\"/></a></p><p><br/></p>','关于SAPA说起 萨帕 ，很多地方翻译成 沙巴 ，容易和东马（ 马来西亚 ）的 沙巴 州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是 越南 边陲 老街省 的一个山中小镇。两者的英文缩写也完全不同， 越南 沙巴 是SAPA，东马 沙巴 州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将 越南 的 沙巴 翻译成 萨帕 ，这样和马蜂窝本身对该地的译名保持统一。在网络上很容易搜到有 关东 马 沙巴 州的旅游信息攻略，而关于 越南 萨帕 的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因， 萨帕 还算是如今 越南 旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少， 亚洲 面孔多是 台湾 、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。越南 的 萨帕 ，在 中国 旅游圈名气不响，但是在国际上，特别是 欧洲 和 越南 本土，绝对是响当当的旅游朝圣地。如果你问 越南 人，自己的国家哪里最美，相信一半的人会说 萨帕 和 岘港 ，而我们国人耳熟能详的 芽庄 、 美奈 、 富国岛 ，根本不会排在其中。同样在欧美旅游圈，来到 越南 旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性， 萨帕 的旅游基础建设以 越南 的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。言归正传，我们先来认识一下游记的主角。 萨帕 ，也叫沙坝、 沙巴 ，是 越南老街 省 萨帕 县的县城所在地， 萨帕 距离老街市约38公里，老街市与我国 云南 省 红河 哈尼族彝族自治州 河口瑶族自治县 交界，我国游客可通过河口---老街口岸进入 越南 ，然后辗转 萨帕 。萨帕 与 越南 第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期， 法国 殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后 法国 殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代 越南 民主 共和 国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。 萨帕 作为旅游区重新向世界游客开放是在1993年以后，随着 越南 政局的稳定，经济的发展， 萨帕 争做国际化旅游区的步伐如今已经大大加快。关于写这篇游记的原因萨帕 一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从 长沙 到 萨帕 理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他 萨帕 ，多数人都是一脸懵逼。“ 马来西亚 ？”为此楼主又要不厌其烦的介绍此 萨帕 非彼 沙巴 ，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解 萨帕 的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。首先：我们来领略一路上的神奇风光。'),(4,'关于SAPA','<h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于SAPA</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\">说起&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，很多地方翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，容易和东马（&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;）的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;边陲&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/191945.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">老街省</a>&nbsp;的一个山中小镇。两者的英文缩写也完全不同，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;是SAPA，东马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;翻译成&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，这样和马蜂窝本身对该地的译名保持统一。<br/><br/>在网络上很容易搜到有&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/60211.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">关东</a>&nbsp;马&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;州的旅游信息攻略，而关于&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;还算是如今&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/52314.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">亚洲</a>&nbsp;面孔多是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12684.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">台湾</a>&nbsp;、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，在&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/21536.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">中国</a>&nbsp;旅游圈名气不响，但是在国际上，特别是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/14383.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">欧洲</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;本土，绝对是响当当的旅游朝圣地。如果你问&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;人，自己的国家哪里最美，相信一半的人会说&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16315.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">岘港</a>&nbsp;，而我们国人耳熟能详的&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16102.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">芽庄</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16105.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">美奈</a>&nbsp;、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/16379.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">富国岛</a>&nbsp;，根本不会排在其中。同样在欧美旅游圈，来到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的旅游基础建设以&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。<br/><br/>言归正传，我们先来认识一下游记的主角。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，也叫沙坝、&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，是&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/65633.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南老街</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;县的县城所在地，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;距离老街市约38公里，老街市与我国&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/12711.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">云南</a>&nbsp;省&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/11871.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">红河</a>&nbsp;哈尼族彝族自治州&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/141503.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">河口瑶族自治县</a>&nbsp;交界，我国游客可通过河口---老街口岸进入&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;，然后辗转&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;。<br/><br/><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;与&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10171.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">法国</a>&nbsp;殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;民主&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/63525.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">共和</a>&nbsp;国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;作为旅游区重新向世界游客开放是在1993年以后，随着&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10180.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">越南</a>&nbsp;政局的稳定，经济的发展，&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;争做国际化旅游区的步伐如今已经大大加快。<br/><br/></p><h2 class=\"t9\" style=\"margin: 0px; padding: 0px; font-size: 24px; height: 50px; background-image: initial; background-position: 0px center; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; overflow: hidden; color: rgb(34, 34, 34); font-weight: normal; line-height: 50px;\"><span class=\"_j_anchor\" style=\"line-height: 50px; display: block;\">关于写这篇游记的原因</span></h2><p class=\"_j_note_content _j_seqitem\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\"><a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10466.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">长沙</a>&nbsp;到&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;，多数人都是一脸懵逼。“&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10097.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">马来西亚</a>&nbsp;？”为此楼主又要不厌其烦的介绍此&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;非彼&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/10760.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">沙巴</a>&nbsp;，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。<br/><img src=\"/tourOne/image/20190829/1567055967177032859.jpg\" title=\"1567055967177032859.jpg\" alt=\"wKgE2l1GNtKAQHOqAA5qv1MAY-M544.jpg\"/>胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/131461.html\" class=\"link _j_keyword_mdd\" target=\"_blank\" style=\"color: rgb(77, 77, 77); text-decoration-line: none; cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">萨帕</a>&nbsp;的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。<br/><img src=\"/tourOne/image/20190829/1567055972728013349.jpg\" title=\"1567055972728013349.jpg\" alt=\"wKgE2l1HheGAaE6OAAXfqZoi9kY776.jpg\"/>首先：我们来领略一路上的神奇风光。</p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117848.html\" style=\"color: rgb(255, 157, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055955749098933.jpg\" alt=\"\"/></a></p><p><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10180/scenery_16782910/825117849.html\" style=\"color: rgb(255, 138, 0); text-decoration-line: none; cursor: pointer; background-color: transparent; outline: 0px; display: block; position: relative; zoom: 1;\"><img class=\"\" src=\"/ueditor/jsp/upload/image/20190829/1567055958872025478.jpg\" alt=\"\"/></a></p><p><br/></p>','关于SAPA说起 萨帕 ，很多地方翻译成 沙巴 ，容易和东马（ 马来西亚 ）的 沙巴 州搞混。其实两者相隔千里，后者是以海景驰名世界的旅游圣地，而前者则是 越南 边陲 老街省 的一个山中小镇。两者的英文缩写也完全不同， 越南 沙巴 是SAPA，东马 沙巴 州是SABAH，大家要注意区别。在后文中，为了方便阅读，我们将 越南 的 沙巴 翻译成 萨帕 ，这样和马蜂窝本身对该地的译名保持统一。在网络上很容易搜到有 关东 马 沙巴 州的旅游信息攻略，而关于 越南 萨帕 的却残缺不全，想来一个连中文译名都没有完全统一的地方，国内游客的关注程度可想而知。正是因为这个原因， 萨帕 还算是如今 越南 旅游的一片陌生之地，就和北河一样，大陆游客的足迹很少， 亚洲 面孔多是 台湾 、香港、韩、日为主。即使有大陆游客，多是成团出游，自由行者寥寥无几，即使有也是停留两日走马观花一番，便匆匆离去。越南 的 萨帕 ，在 中国 旅游圈名气不响，但是在国际上，特别是 欧洲 和 越南 本土，绝对是响当当的旅游朝圣地。如果你问 越南 人，自己的国家哪里最美，相信一半的人会说 萨帕 和 岘港 ，而我们国人耳熟能详的 芽庄 、 美奈 、 富国岛 ，根本不会排在其中。同样在欧美旅游圈，来到 越南 旅游，几乎每个旅行者，都会不远万里来到这个边陲小镇打卡，完成一次乡间徒步。其中的缘由，楼主游记里会慢慢道来。也正是因为这一特性， 萨帕 的旅游基础建设以 越南 的角度来说非常先进合理，当地的旅游接待也非常专业，游客在这里可以非常舒适的观光和度假。言归正传，我们先来认识一下游记的主角。 萨帕 ，也叫沙坝、 沙巴 ，是 越南老街 省 萨帕 县的县城所在地， 萨帕 距离老街市约38公里，老街市与我国 云南 省 红河 哈尼族彝族自治州 河口瑶族自治县 交界，我国游客可通过河口---老街口岸进入 越南 ，然后辗转 萨帕 。萨帕 与 越南 第一高峰番西邦峰毗邻，四面高山环绕，海拔高，温差大。早年这里是苗族、彝族等少数民族聚居区，当地居民以梯田农耕为生。19世纪后期， 法国 殖民军和传教士来到了这个山间村落。由于当地独特的自然条件，1912年法军在此建立了军事疗养院，而后 法国 殖民者以此地为避暑胜地，陆续修建了大量的别墅、度假屋和基础设施。摆脱殖民统治后，二十世纪60年代 越南 民主 共和 国时期，政府颁布了“新经济区移民方案”，以京族为主的很多低地移民开始移民到小镇，小镇再次焕发活力。后来因中越战争的地缘位置缘故，小镇逐渐衰落。 萨帕 作为旅游区重新向世界游客开放是在1993年以后，随着 越南 政局的稳定，经济的发展， 萨帕 争做国际化旅游区的步伐如今已经大大加快。关于写这篇游记的原因萨帕 一直作为楼主夏季的歇凉胜地，每到夏天最热的时候，或者春夏、夏秋交接之时，都会考虑来这里度假。经常一住就是一星期。因为交通便利，从 长沙 到 萨帕 理论上一天就到，比之国内人山人海的旅游区，这里该静的地方静、该闹的时候闹，节奏轻快，生活悠闲，的确是可以好好放松自己的一块福地。最近，异常高温天气持续，酷暑难耐，楼主的圈子里经常有人问“有没有哪里可以避暑啊？”楼主告诉他 萨帕 ，多数人都是一脸懵逼。“ 马来西亚 ？”为此楼主又要不厌其烦的介绍此 萨帕 非彼 沙巴 ，然后就交通、住宿、景点、吃喝玩乐等等一系列问题详细解说。后来楼主想要偷懒，打发朋友去查马蜂窝、穷游一类网站的攻略，结果自己一查，发现根本就没有相关的详细资料，各种最基础，最实用的信息，都散落在形形色色的游记里。于是为了方便自己的朋友，也为了方便广大蜂友，正好楼主手头也有上次出游保留的一些资料，就有了写这篇游记的想法。胖大王游记老规矩，喜欢听楼主胡说八道的，咱们凳子搬好，花生茶水到位，准备开张。那些实用主义者，请翻看目录，你需要的最基础、最实用、最清晰的攻略，都会以贴士的形式总结。希望这篇游记可以带着大家了解 萨帕 的前生今世，风土人情。也能为夯实马蜂窝的SAPA地方攻略尽自己的绵薄之力。首先：我们来领略一路上的神奇风光。');

/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `u_id` int(11) NOT NULL,
  `s_id` int(15) NOT NULL,
  `comment` varchar(400) NOT NULL,
  `comment_date` date DEFAULT NULL,
  KEY `u_id` (`u_id`),
  KEY `s_id` (`s_id`),
  CONSTRAINT `comment_ibfk_1` FOREIGN KEY (`u_id`) REFERENCES `user` (`u_id`),
  CONSTRAINT `comment_ibfk_2` FOREIGN KEY (`s_id`) REFERENCES `scenery` (`s_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `comment` */

insert  into `comment`(`u_id`,`s_id`,`comment`,`comment_date`) values (1,1,'这里的风景也太美了，我很喜欢！希望大家也能够喜欢！','2018-02-15'),(2,1,'世界上居然有如此美丽的风景，简直是神的杰作，我真的太喜欢这里了！','2018-03-14');

/*Table structure for table `scenery` */

DROP TABLE IF EXISTS `scenery`;

CREATE TABLE `scenery` (
  `s_id` int(15) NOT NULL,
  `s_name` varchar(50) NOT NULL,
  `s_location` varchar(50) NOT NULL,
  `s_synopsis` varchar(1500) NOT NULL,
  `s_images` varchar(500) NOT NULL,
  `s_type` varchar(100) NOT NULL,
  PRIMARY KEY (`s_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `scenery` */

insert  into `scenery`(`s_id`,`s_name`,`s_location`,`s_synopsis`,`s_images`,`s_type`) values (1,'亚丁稻城','四川省甘孜藏族自治州稻城县香格里拉镇亚丁村','稻城亚丁风景区位于四川省甘孜藏族自治州稻城县香格里拉镇亚丁村境内，主要由“仙乃日、央迈勇、夏诺多吉”三座神山和周围的河流、湖泊和高山草甸组成，它的景致保持着在地球上近绝迹的纯粹，因其独特的地貌和原生态的自然风光，被誉为“香格里拉之魂”和“最后的香格里拉”，被国际友人誉为“水蓝色星球上的最后一片净土”，是摄影爱好者的天堂。\n景区内不仅有壮丽神圣的雪山，还有辽阔的草甸、五彩斑斓的森林和碧蓝通透的海子，雪域高原最美的一切几乎都汇聚于此，这一切的一切都让人流连忘返。如当地虔诚的藏民一样，徒步转山是感受亚丁风光的最好方式。不过由于亚丁保护区海拔较高，全程徒步还是需要相当的体力。\n亚丁，藏语意为“向阳之地”。保护区内的三座雪山：仙乃日、央迈勇、夏诺多吉，南北向分布，呈品字形排列，统称“念青贡嘎日松贡布”，意为：终年积雪不化的三座护法神山圣地。藏传佛教中称其为“三怙主雪山”，是藏民心中的神圣之地。\n1928年，美国植物学家、探险家约瑟夫•洛克到达此地，回国后在美国《国家地理杂志》上撰文并刊登所摄照片，将亚丁介绍给了全世界。\n','http://img1.imgtn.bdimg.com/it/u=2036297746,586305381&fm=26&gp=0.jpg','河川;湖泊;人文风景'),(2,'丽江','云南省西北部滇川藏三省区交界处','丽江，云南省地级市，位于云南省西北部，滇川藏三省区交界处，是国际知名旅游城市   、古代“南方丝绸之路”和“茶马古道”的重要通道   。市区中心位于东经100°25，北纬26°86，总面积2.06万平方公里，北连迪庆藏族自治州，南接大理白族自治州，西邻怒江傈僳族自治州，东与四川凉山彝族自治州和攀枝花市接壤，距昆明市527公里 \n丽江市位于云贵高原与青藏高原的连接部位，地势西北高而东南低，最高点为玉龙雪山主峰，海拔一般都在2000米以上，属低纬暖温带高原山地季风气候，境内水利资源丰富，动植物种类繁多，为云南省重点林区、中国水电西电东送基地之一。 \n截至2015年，丽江市辖1个市辖区，4个县。2016年末，全市常住人口128.5万人。2016年，全年完成地区生产总值310.1799亿元，人均地区生产总值24186元。 \n丽江是一个多民族聚居的地方，除汉族外，共有12个世居少数民族，22种少数民族；丽江是著名的旅游城市，拥有世界文化遗产丽江古城、世界自然遗产三江并流、世界记忆遗产纳西族东巴古籍文献三大世界遗产。 旅游资源丰富，有旅游风景点104处，有丽江古城、玉龙雪山、虎跳峡、老君山、束河古镇、阿纳果 等知名旅游景点。 先后被评为中国旅游竞争力百强城市  、中国十大休闲城市 、中国优秀旅游城市 、中国十大魅力城市 、云南省人才特区 、长江经济带上的绿色明珠  等荣誉称号.\n','https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=424228002,3844299264&fm=26&gp=0.jpg','湖泊;人文风景'),(3,'九寨沟','四川省阿坝藏族羌族自治州九寨沟县','九寨沟：世界自然遗产、国家重点风景名胜区、国家AAAAA级旅游景区、国家级自然保护区、国家地质公园、世界生物圈保护区网络，是中国第一个以保护自然风景为主要目的的自然保护区。\n九寨沟位于四川省阿坝藏族羌族自治州九寨沟县境内，地处青藏高原、川西高原、山地向四川盆地过渡地带，南距成都市300多公里，是一条纵深50余千米的山沟谷地，总面积64297公顷，森林覆盖率超过80%。因沟内有树正寨、荷叶寨、则查洼寨等九个藏族村寨坐落在这片高山湖泊群中而得名。\n九寨沟国家级自然保护区主要保护对象是以大熊猫、金丝猴等珍稀动物及其自然生态环境。有74种国家保护珍稀植物，有18种国家保护动物，还有丰富的古生物化石、古冰川地貌。\n“九寨归来不看水”，是对九寨沟景色真实的诠释。泉、瀑、河、滩108个海子，构成一个个五彩斑斓的瑶池玉盆。长海、剑岩、诺日朗、树正、扎如、黑海六大景观，呈“Y”字形分布。翠海、叠瀑、彩林、雪峰、藏情、蓝冰，被称为“六绝”。神奇的九寨，被世人誉为“童话世界”，号称“水景之王”。\n受地震影响，九寨沟景区于2017年8月9日起停止接待游客。 [1] \n2018年3月8日起，九寨沟景区部份景观将恢复开放。开放区域为九寨沟景区沟口至长海段沿途景观，实行专车专导，团队游览方式\n','https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=3089504479,2014820585&fm=26&gp=0.jpg','山岳;湖泊;森林'),(4,'香格里拉','云南省西北部青藏高原横断山区腹地','香格里拉（Shangri-la）藏语意为“心中的日月”，是云南省迪庆藏族自治州下辖市及首府所在地，位于云南省西北部、青藏高原横断山区腹地，是滇、川、藏三省区交界地，也是世界自然遗产“三江并流”景区所在地。\n截至2014年，香格里拉市总面积11613平方公里，辖4个镇、7个乡，共有6个社区、58个行政村。2011年年末，香格里拉市总人口为174,585人，除主体民族藏族外还有汉族、纳西族、彝族、白族等十几个民族，人口密度为10人/平方公里，是云南省面积最大、人口密度最低的市份之一。2011年，香格里拉实现县级生产总值359,580万元，市级一、二、三产业增加值占全县生总产值的比重为9.4:38.3:52.3。\n香格里拉于20世纪30年代出现于英国作家詹姆斯•希尔顿的著名小说《Lost Horizen（消失的地平线）》中而为世人所向往，不久便被拍成同名电影并荣获多项奥斯卡奖，更使其为世人熟知。香格里拉藏区历史悠久，自然风光绚丽，拥有普达措国家公园、独克宗古城、噶丹松赞林寺、虎跳峡等景点。2019年4月30日，云南省人民政府决定香格里拉退出贫困县序列。\n','https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=1858715120,2875168820&fm=26&gp=0.jpg','山岳;人文风景'),(5,'桂林山水','广西东北部','桂林山水是对桂林旅游资源的总称.国家AAAAA级旅游景区，中国十大风景名胜之一，桂林山水是中国山水的代表闻名，千百年来享有<桂林山水甲天下>的美誉。\n桂林市是世界著名的风景游览城市和中国历史文化名城，是万年智慧圣地，是中国陶器起源地之一，是目前世界上唯一具有三处万年古陶遗址（甑皮岩、大岩、庙岩）的城市，桂林甑皮岩发现的陶雏器填补世界陶器起源空白点。\n<万年智慧>是桂林重量级文化名片，甑皮岩国家考古遗址公园是桂林向世界展现中华民族“万年智慧”的历史文化名片.\n桂林拥有世界自然遗产桂林山水、世界灌溉遗产灵渠两大世界遗产。\n2014年6月，第38届世界遗产大会上以桂林为首的中国南方喀斯特第二期项目申遗成功，成为世界自然遗产，桂林山水荣登世界自然遗产名录\n2016年9月，中国社会科学院考古研究所、广西考古所等考古机构认为：桂林甑皮岩先民是具有高智商的智慧人，双料混炼技术是万年前人类的发明，桂林是万年人类智慧圣地进行了揭牌。\n2018年8月，桂林市兴安灵渠列入世界灌溉工程遗产名录，成为世界灌溉工程遗产.\n','https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=3328010001,994506162&fm=26&gp=0.jpg','湖泊;山岳'),(6,'长城','分布在河北、北京、天津、山西、陕西、甘肃等','长城（The Great Wall），又称万里长城，是中国古代的军事防御工程，是一道高大、坚固而连绵不断的长垣，用以限隔敌骑的行动。长城不是一道单纯孤立的城墙，而是以城墙为主体，同大量的城、障、亭、标相结合的防御体系。\n长城修筑的历史可上溯到西周时期，发生在首都镐京（今陕西西安）的著名的典故“烽火戏诸侯”就源于此。春秋战国时期列国争霸，互相防守，长城修筑进入第一个高潮，但此时修筑的长度都比较短。秦灭六国统一天下后，秦始皇连接和修缮战国长城，始有万里长城之称。明朝是最后一个大修长城的朝代，今天人们所看到的长城多是此时修筑。\n长城资源主要分布在河北、北京、天津、山西、陕西、甘肃、内蒙古、黑龙江、吉林、辽宁、山东、河南、青海、宁夏、新疆等15个省区市。其中陕西省是中国长城资源最为丰富的省份，境内长城长度达1838千米。根据文物和测绘部门的全国性长城资源调查结果，明长城总长度为8851.8千米，秦汉及早期长城超过1万千米，总长超过2.1万千米。\n1961年3月4日，长城被国务院公布为第一批全国重点文物保护单位。1987年12月，长城被列入世界文化遗产。\n','https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=1449639491,1102252939&fm=26&gp=0.jpg','山岳;人文风景'),(7,'张家界','中国湖南省','张家界，原名“大庸市”，湖南省地级市。辖2个市辖区（永定区、武陵源区）、2个县（慈利县、桑植县）。位于湖南西北部，澧水中上游，属武陵山区腹地。 [1]  张家界因旅游建市，是中国最重要的旅游城市之一，是湘鄂渝黔革命根据地的发源地和中心区域。\n1982年9月，张家界国家森林公园成为中国第一个国家森林公园。\n1988年8月，张家界武陵源风景名胜区被列入国家重点风景名胜区；1992年，由张家界国家森林公园等三大景区构成的武陵源风景名胜区被联合国教科文组织列入《世界自然遗产名录》；2004年2月，被列入全球首批《世界地质公园》；2007年，被列入中国首批国家5A级旅游景区。 [2]  2017年，被授予“国家森林城市”荣誉称号。\n','https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=2408428625,3324160150&fm=26&gp=0.jpg','山岳'),(8,'三亚','中国华南海南省','三亚，简称崖，古称崖州，别称鹿城。是海南省下辖地级市，位于海南岛的最南端。三亚东邻陵水县，西接乐东县，北毗保亭县，南临南海，介于北纬18°09′34″~18°37′27″、东经108°56′30″~109°48′28″之间。 [1]  三亚市陆地总面积1919.58平方千米，海域总面积6000平方千米。东西长91.6千米，南北宽51公里，下辖四个区。 [2]  2018年，全市年末户籍人口614647人，聚居了汉族、黎族、苗族等20多个民族。 [3] \n三亚是具有热带海滨风景特色的国际旅游城市 [4]  ，又被称为“东方夏威夷”。 [5]  2016年6月14日，中国科学院对外发布《中国宜居城市研究报告》，三亚宜居指数在全国40个城市中位居第三。 [6]  2016年9月，三亚入选“中国地级市民生发展100强。 [7] 2017年2月，三亚入选第三批国家低碳城市试点之一。三亚也是同时入选中国特色魅力城市200强及世界特色魅力城市200强。\n2017年中国地级市全面小康指数排名第50 [8]  。2018年，三亚市地区生产总值595.51亿元人民币。 [3] \n2020年11月底至12月初，第六届亚洲沙滩运动会将在海南省三亚市举行。2018年12月，入选2018中国最佳旅游目的地城市第15名。 [9-10]  2018年12月13日，入选中国特色农产品优势区名单。 [11]  三亚是“无废城市”建设试点城市。\n','https://ss1.bdstatic.com/70cFvXSh_Q1YnxGkpoWK1HF6hhy/it/u=4150524191,2691694938&fm=26&gp=0.jpg','海岛海滨'),(9,'西湖','浙江省杭州市龙井西湖路1号','西湖，位于浙江省杭州市西湖区龙井路1号，杭州市区西部，景区总面积49平方千米，汇水面积为21.22平方千米，湖面面积为6.38平方千米。\n西湖南、西、北三面环山，湖中白堤、苏堤、杨公堤、赵公堤将湖面分割成若干水面。西湖的湖体轮廓呈近椭圆形，湖底部较为平坦，湖泊平均水深为2.27米，最深约5米，最浅不到1米。湖泊天然地表水源是金沙涧、龙泓涧、赤山涧（慧因涧）、长桥溪四条溪流。西湖地处中国东南丘陵边缘和中亚热带北缘，年均太阳总幅射量在100—110千卡/平方厘米之间，日照时数1800—2100小时。 [1-2] \n西湖有100多处公园景点，有“西湖十景”、“新西湖十景”、“三评西湖十景”之说，有60多处国家、省、市级重点文物保护单位和20多座博物馆，有断桥、雷峰塔、钱王祠、净慈寺、苏小小墓等景点。 [3] \n2007年，杭州市西湖风景名胜区被评为“国家AAAAA级旅游景区”。 [4]  2011年6月24日，“杭州西湖文化景观”正式被列入《世界遗产名录》。\n','https://ss1.bdstatic.com/70cFvXSh_Q1YnxGkpoWK1HF6hhy/it/u=2858772857,2695358562&fm=26&gp=0.jpg','人文风景;湖泊'),(10,'神农架','华中地区湖北省','神农架林区，简称神农架，1970年经国务院批准建制，直属湖北省管辖，是中国唯一以“林区”命名的行政区划。处于武当山，神农架，长江三峡组成的旅游带上。神农架林区位于湖北省西部，东与湖北省襄阳市保康县接壤，西与重庆市巫山县毗邻，南依兴山、巴东而濒长江三峡，北倚十堰市房县、竹山县，远眺武当山风景区。地跨东经109°56′～110°58′，北纬31°15′～31°75′，全区总面积3253平方公里，坐拥联合国“世界地质公园”，辖6镇2乡和1个国家级自然保护区、1个国有森工企业林业管理局、1个国家湿地公园，林地占85%以上。\n2013年，神农架林区全区实现地区生产总值18.5745亿元，比2012年增长9.8%。其中：第一产业增加值1.7723亿元，比2012年增长5.0%；第二产业增加值7.6665亿元，比2012年增长9.7%；第三产业增加值9.1357亿元，比2012年增长10.9%。三次产业结构比重由2012年的9.8：42.1：48.1调整为9.5：41.3：49.2。 [1] \n神农架是中国首个获得联合国教科文组织人与生物圈自然保护区、世界地质公园、世界遗产三大保护制度共同录入的“三冠王”名录遗产地。 [2]  2016年7月17日，正在土耳其伊斯坦布尔举行的第40届世界遗产大会上，中国湖北神农架被列入世界遗产名录 [3]  ，成为湖北省第1处、中国第11处世界自然遗产。 [2]  [4]  2018年8月7日，湖北省政府批准神农架林区退出贫困县。 [5] 2018年9月，荣获“中国天然氧吧”称号。 [6]  2019年3月，被列为第一批革命文物保护利用片区分县名单。\n','https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=1289404663,1794181612&fm=26&gp=0.jpg','山岳;人文风景'),(11,'美国大峡谷','美国亚利桑那州西北部','美国大峡谷（Grand Canyon）是一个举世闻名的自然奇观，科罗拉多河穿流其中，它是联合国教科文组织选为受保护的天然遗产之一。大峡谷位于美国亚利桑那州西北部的凯巴布高原上，是地球上最为壮丽的景色之一。\n科罗拉多河在科罗拉多高原上共切割出19条主要峡谷，总面积为2724.7平方公里，其中最深、最宽、最长的一个就是大峡谷。它全长446公里，是世界上最长的峡谷之一。峡谷顶宽6至28公里，最深处1800米。谷底水面不足 1000米宽，夏季冰雪融水，水深增至18米。山石多为红色。\n','https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=3768987325,959566919&fm=26&gp=0.jpg','山岳'),(12,'镜泊湖火山','黑龙江镜泊湖西北约50公里','镜泊湖火山位于黑龙江镜泊湖西北约50公里，在张广才岭海拔1000米的深山区。可以见到13个保存完好的火山口。从火山口内壁可观察到火山以喷发和溢流交替方式活动。区内熔岩隧道发育。火山喷发物阻塞了牡丹江，形成了我国最大的火山堰塞湖-镜泊湖。在方圆20公里范围内有内岩壁陡峭形状不同的七个火山口连在一起，在3号与4号火山口之间，有熔岩隧道相通。镜泊火山是爆发的休眠火山。经千万年沧桑变化，成为低陷的原始林带，故称火山口原始森林。镜泊火山是爆发的休眠火山。大约一万年前爆发，形成大小不等、形状不一的10个火山口。经千万年沧桑变化，成为低陷的原始林带，故称火山口原始森林。附近处有小北湖、鸳鸯池及熔岩洞等景观，构成奇特的熔岩风光。1958年森林调查队给命名为\"地下森林\"。1993年林业部批准为国家级森林公园。有旅游公路直达，登山观光已修有石径、栏杆及观览亭、停车场等设施，年接待游客达10万人次以上。\n　　放射性碳测年代，近年来有过三次爆发，分别是公元前500年，公元前1500年和公元前3500年前。5500（公元前3500年）年前的喷发最大，喷射熔岩流覆盖500平方公里，阻塞了滔滔奔腾的牡丹江水。冷却后的岩浆堆积在牡丹江河道上，像一座大坝一样把牡丹江拦腰截断了。坝上游河段便形成了火山堰塞湖——镜泊湖，湖水又通过熔岩坝的一些断裂缝隙，渗硫出来，跌落崖壁，于是便形成吊水楼瀑布。','https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=781538384,2012191149&fm=26&gp=0.jpg','岩溶;火山'),(13,'长白山天池火山','长白山地区','长白山天池火山是目前我国境内保存最为完整的新生代多成因复合火山，火山活动经历了造盾（2.77-1.203Ma早更新世）、造锥（1.12-0.04Ma中－晚更新世）和全新世喷发三个发展阶段，三个阶段岩浆成分从玄武质→粗面质→碱流质代表其演化过程。历史上长白山地区有过多次喷发的“史料记载\"，1668年和1702年两次天池火山喷发是可信的。\n通过火山地质学和精细的14C年代学研究，全新世以来天池火山至少有两次（公元1199年和约5000年前）大规模喷发。\n公元1199～1201年天池火山大喷发是全球近2000年来最大的一次喷发事件，当时喷出的火山灰降落到远至日本海及日本北部。\n1997年国家“九五”重点项目“中国若干近代若干活动火山的监测与研究”启动。\n中国地震局在长白山天池火山区开始了监测和研究工作。\n经过几年的研究，专家们认为，长白山是一个休眠的活火山，虽然休眠了300年，但世界上休眠数百年再次喷发的火山并不少见。\n地球物理探测表明，长白山天池下方有地壳岩浆囊存在的迹象，\n长白山天池具有再次喷发的危险，其喷发形式为爆炸式，由于天池20亿吨水的存在，是喷发具更大的破坏性。\n“九五”期间，中国地震局、吉林省地震局在长白山天池建立了火山监测站，包括：数字地震监测台网、定点形变观测系统、GPS流动观测网、地球化学观测网。“九五”项目的实施结束了对天池火山不设防的局面。\n从目前的观测结果看，尚没有发现火山复苏的征兆，人们可放心的尽享大自然赐予长白山天池的丰富资源和优美景观。','https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=3617038892,120903060&fm=26&gp=0.jpg','火山;湖泊'),(14,'吉林龙岗火山','吉林省辉南县东部','吉林龙岗火山龙岗火山群位于吉林省辉南县东部，有170余座火山，除8个火山口湖外，其余为火山渣锥。该龙湾群是目前国内所发现的最大的火山口湖群。被誉为“长白山小天池”。同三角龙湾相比，大龙湾的特点是湖面开阔，湖景多变，别有天地。它在6个龙湾中面积最大，由于南侧湖面被溶岩冲塌，使湖面曲折，形成东西两个袖湾和袖湖。\n如果说大龙湾是开阔与神秘交融，那么东龙湾则是幽静与险峻相伴。　这是尚未开发的处女湖，它静静地卧在起伏的群山中，这里古木参天，莽草无崖，礁石林立，只有空山野水，可闻鸟语泉声。一切都是那样原始，那样古朴，那样自然，给人第一感觉是幽静和安详。当你走近它时，就会强烈地感到它是那样的险峻和奇奥。\n东龙湾属于强烈暴发性层状火山湖，面积为0.4平方公里，水最深处122米，仅次于长白山天池。沿它西行至8公里，小巧玲珑的南龙湾展现在面前。南龙湾呈瓶状，东西延伸，湖面约0.2平方公里。在青山映衬下，一池湖水碧波荡漾，景色十分典雅，撩人心扉，楚楚动人。二龙湾和小龙湾地处小形盆地，小龙湾居西，二龙湾居东，中间有条小河穿过，站在附近的金龙顶子俯视，尤如一个大自然的艺术盆景。由于两湖处在高山沟谷间，湿气蒸郁不散，白云水雾经常飘荡于峰顶山腰，给人一种云雾迷离的神奇色彩。\n龙湾千重秀，林木百里深。龙湾不仅是个风光胜地，而且不是个宝贵的资源王国。这里植被各类丰富，森林茂密，有500多种生植物。人参、天麻、党参等珍贵药用植物居多，猕猴桃、山葡萄贮量很大，蕨菜、元蘑、榆黄蘑、刺嫩芽等山珍资源丰。有56种野生动物在这里繁衍生息。野兔、狐狸、狍子经常出没山林，飞龙、野鸡栖息林间，蟾蜍、田鸡在溪流沟谷间随处可见，人工驯养的梅花鹿数达千只。','https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=4275213507,1472982525&fm=15&gp=0.jpg','火山;山岳'),(15,'好望角','非洲西南角','好望角（葡萄牙文：Cabo da Boa Esperança，英文Cape of Good Hope）意思是“美好希望的海角”，是非洲西南端非常著名的岬角，位于34°21′25″S，18°29′51″E处。北距南非共和国的开普敦市52千米。因多暴风雨，海浪汹涌，故最初称为“风暴角”。\n1939年这里成为自然保护区，好望角东方2千米处，并设有一座灯塔。\n好望角是西方的探险家欲为通往富庶的东方航道，故改称好望角，苏伊士运河通航前，来往于亚欧之间的船舶都经过好望角。特大油轮无法进入苏伊士运河，仍需取此道航行。\n好望角常被误认为是非洲大陆最南端，然距离其东南偏东方向约150km、隔佛尔斯湾而望的厄加勒斯角才是实至名归的非洲最南端。\n好望角，地处来自印度洋的温暖的莫桑比克厄加勒斯洋流和来自南极洲水域的寒冷的本格拉洋流的汇合处。强劲的西风急流掀起的惊涛骇浪常年不断，这里除风暴为害外，还常常有“杀人浪”出现。这种海浪前部犹如悬崖峭壁，后部则像缓缓的山坡，波高一般有15-20米，在冬季频繁出现，还不时加上极地风引起的旋转浪，当这两种海浪叠加在一起时，海况更加恶劣，而且还有一股很强的沿岸流。当浪与流相遇时，整个海面如同开锅似的翻滚，航行到这里的船舶往往遭难，因此好望角成为世界上最危险的航海地段。\n','https://ss0.bdstatic.com/70cFuHSh_Q1YnxGkpoWK1HF6hhy/it/u=4058207973,2414009567&fm=26&gp=0.jpg','海岛海滨;湖泊');

/*Table structure for table `score` */

DROP TABLE IF EXISTS `score`;

CREATE TABLE `score` (
  `sc_u_id` int(11) NOT NULL,
  `sc_s_id` int(15) NOT NULL,
  `score` int(15) NOT NULL,
  KEY `sc_u_id` (`sc_u_id`),
  KEY `sc_s_id` (`sc_s_id`),
  CONSTRAINT `score_ibfk_1` FOREIGN KEY (`sc_u_id`) REFERENCES `user` (`u_id`),
  CONSTRAINT `score_ibfk_2` FOREIGN KEY (`sc_s_id`) REFERENCES `scenery` (`s_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

/*Data for the table `score` */

insert  into `score`(`sc_u_id`,`sc_s_id`,`score`) values (1,1,8),(1,6,6),(1,10,8),(1,3,6),(1,14,7),(1,5,9),(1,1,8);

/*Table structure for table `user` */

DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `u_id` int(11) NOT NULL AUTO_INCREMENT,
  `u_name` varchar(50) NOT NULL,
  `u_password` varchar(20) NOT NULL,
  `u_phone` varchar(20) NOT NULL,
  `u_age` int(10) NOT NULL,
  `u_sex` varchar(5) NOT NULL,
  `u_like` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`u_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `user` */

insert  into `user`(`u_id`,`u_name`,`u_password`,`u_phone`,`u_age`,`u_sex`,`u_like`) values (1,'hanfeng','123456','15196093918',22,'男',NULL),(2,'yangyuqiao','123456','15729873676',21,'男',NULL);

